                $noteText .= "\nSuggested: {$suggestedTeknisi->name}";
            }

            // Add internal note
            TicketComment::create([
                'ticket_id' => $ticket->id,
                'user_nip' => $teknisi->nip,
                'comment' => $noteText,
                'is_internal' => true,
